from os.path import join, splitext
from sklearn.linear_model import LinearRegression

global estimator, estimatorFilename

estimator = estimator._final_estimator
assert isinstance(estimator, LinearRegression)

filename = '{}.info.txt'.format(splitext(estimatorFilename)[0])
with open(filename, mode='w') as f:

    f.write('\ncoef_\n')
    for l in estimator.coef_.T:
        f.write(', '.join(l.astype(str)))
        f.write('\n')

    f.write('\nintercept_\n')
    f.write(', '.join(estimator.intercept_.astype(str)))
    f.write('\n')

    f.write('\nhttps://scikit-learn.org/stable/modules/generated/sklearn.linear_model.LinearRegression.html\n')
